var now = new Date();
function createtime() {
  // ÂΩìÂâçÊó∂Èó¥
  now.setTime(now.getTime() + 1000);
  var start = new Date("08/01/2022 00:00:00"); // ÊóÖË°åËÄÖ1Âè∑ÂºÄÂßãËÆ°ÁÆóÁöÑÊó∂Èó¥
  var dis = Math.trunc(23400000000 + ((now - start) / 1000) * 17); // Ë∑ùÁ¶ª=ÁßíÊï∞*ÈÄüÂ∫¶ ËÆ∞‰ΩèËΩ¨Êç¢ÊØ´Áßí
  var unit = (dis / 149600000).toFixed(6);  // Â§©ÊñáÂçï‰Ωç
  // ÁΩëÁ´ôËØûÁîüÊó∂Èó¥
  var grt = new Date("08/09/2022 00:00:00");
  var days = (now - grt) / 1e3 / 60 / 60 / 24,
    dnum = Math.floor(days),
    hours = (now - grt) / 1e3 / 60 / 60 - 24 * dnum,
    hnum = Math.floor(hours);
  1 == String(hnum).length && (hnum = "0" + hnum);
  var minutes = (now - grt) / 1e3 / 60 - 1440 * dnum - 60 * hnum,
    mnum = Math.floor(minutes);
  1 == String(mnum).length && (mnum = "0" + mnum);
  var seconds = (now - grt) / 1e3 - 86400 * dnum - 3600 * hnum - 60 * mnum,
    snum = Math.round(seconds);
  1 == String(snum).length && (snum = "0" + snum);
  let currentTimeHtml = "";
  (currentTimeHtml =
    hnum < 18 && hnum >= 9
      ? `<img class='boardsign' src='https://lskypro.acozycotage.net/Fomalhaut/badge/FÂ∞èÂ±ã-ÁßëÁ†îÊë∏È±º‰∏≠.svg' title='‰ªÄ‰πàÊó∂ÂÄôËÉΩÂ§üÂÆûÁé∞Ë¥¢ÂØåËá™Áî±ÂëÄ~'><br> <div style="font-size:13px;font-weight:bold">Êú¨Á´ôÂ±ÖÁÑ∂ËøêË°å‰∫Ü ${dnum} Â§© ${hnum} Â∞èÊó∂ ${mnum} ÂàÜ ${snum} Áßí <i id="heartbeat" class='fas fa-heartbeat'></i> <br> ÊóÖË°åËÄÖ 1 Âè∑ÂΩìÂâçË∑ùÁ¶ªÂú∞ÁêÉ ${dis} ÂçÉÁ±≥ÔºåÁ∫¶‰∏∫ ${unit} ‰∏™Â§©ÊñáÂçï‰Ωç üöÄ</div>`
      : `<img class='boardsign' src='https://lskypro.acozycotage.net/Fomalhaut/badge/FÂ∞èÂ±ã-‰∏ãÁè≠‰ºëÊÅØÂï¶.svg' title='‰∏ãÁè≠‰∫ÜÂ∞±ËØ•ÂºÄÂºÄÂøÉÂøÉÂú∞Áé©ËÄç~'><br> <div style="font-size:13px;font-weight:bold">Êú¨Á´ôÂ±ÖÁÑ∂ËøêË°å‰∫Ü ${dnum} Â§© ${hnum} Â∞èÊó∂ ${mnum} ÂàÜ ${snum} Áßí <i id="heartbeat" class='fas fa-heartbeat'></i> <br> ÊóÖË°åËÄÖ 1 Âè∑ÂΩìÂâçË∑ùÁ¶ªÂú∞ÁêÉ ${dis} ÂçÉÁ±≥ÔºåÁ∫¶‰∏∫ ${unit} ‰∏™Â§©ÊñáÂçï‰Ωç üöÄ</div>`),
    document.getElementById("workboard") &&
    (document.getElementById("workboard").innerHTML = currentTimeHtml);
}
