function addLongtabListener(target, callback) {
  let timer = 0 // ÂàùÂßãÂåñtimer

  target.ontouchstart = () => {
    timer = 0 // ÈáçÁΩÆtimer
    timer = setTimeout(() => {
      callback();
      timer = 0
    }, 380) // Ë∂ÖÊó∂Âô®ËÉΩÊàêÂäüÊâßË°åÔºåËØ¥ÊòéÊòØÈïøÊåâ
  }

  target.ontouchmove = () => {
    clearTimeout(timer) // Â¶ÇÊûúÊù•Âà∞ËøôÈáåÔºåËØ¥ÊòéÊòØÊªëÂä®
    timer = 0
  }

  target.ontouchend = () => { // Âà∞ËøôÈáåÂ¶ÇÊûútimerÊúâÂÄºÔºåËØ¥ÊòéÊ≠§Ëß¶Êë∏Êó∂Èó¥‰∏çË∂≥380msÔºåÊòØÁÇπÂáª
    if (timer) {
      clearTimeout(timer)
    }
  }
}

