    result = Math.round(a / b * 100), // ËÆ°ÁÆóÁôæÂàÜÊØî
    btn = document.querySelector("#go-up"); // Ëé∑ÂèñÊåâÈíÆ

  if (result < 95) { // Â¶ÇÊûúÈòÖËØªËøõÂ∫¶Â∞è‰∫é95% Â∞±ÊòæÁ§∫ÁôæÂàÜÊØî
