/* fpsÊ£ÄÊµã start */
if (window.localStorage.getItem("fpson") == undefined || window.localStorage.getItem("fpson") == "1") {
  var rAF = function () {
    return (
      window.requestAnimationFrame ||
      window.webkitRequestAnimationFrame ||
      function (callback) {
        window.setTimeout(callback, 1000 / 60);
      }
    );
  }();
  var frame = 0;
  var allFrameCount = 0;
  var lastTime = Date.now();
  var lastFameTime = Date.now();
  var loop = function () {
    var now = Date.now();
    var fs = (now - lastFameTime);
    var fps = Math.round(1000 / fs);

    lastFameTime = now;
    // ‰∏çÁΩÆ 0ÔºåÂú®Âä®ÁîªÁöÑÂºÄÂ§¥ÂèäÁªìÂ∞æËÆ∞ÂΩïÊ≠§ÂÄºÁöÑÂ∑ÆÂÄºÁÆóÂá∫ FPS
    allFrameCount++;
    frame++;

    if (now > 1000 + lastTime) {
      var fps = Math.round((frame * 1000) / (now - lastTime));
      if (fps <= 5) {
        var kd = `<span style="color:#bd0000">Âç°Êàêpptü§¢</span>`
      } else if (fps <= 15) {
        var kd = `<span style="color:red">ÁîµÁ´ûÁ∫ßÂ∏ßÁéáüòñ</span>`
      } else if (fps <= 25) {
        var kd = `<span style="color:orange">ÊúâÁÇπÈöæÂèóüò®</span>`
      } else if (fps < 35) {
        var kd = `<span style="color:#9338e6">‰∏çÂ§™ÊµÅÁïÖüôÑ</span>`
      } else if (fps <= 45) {
        var kd = `<span style="color:#08b7e4">Ëøò‰∏çÈîôÂì¶üòÅ</span>`
      } else {
        var kd = `<span style="color:#39c5bb">ÂçÅÂàÜÊµÅÁïÖü§£</span>`
      }
      document.getElementById("fps").innerHTML = `FPS:${fps} ${kd}`;
      frame = 0;
      lastTime = now;
    };

    rAF(loop);
  }

  loop();
} else {
  document.getElementById("fps").style = "display:none!important"
}
